function checkPrime(numbers) {
 if (numbers.length !== 5) {
 console.error("Array must contain exactly 5 numbers.");
 return;
 }

 for (let i = 0; i < numbers.length; i++) {
 let number = numbers[i];

 if (number < 2) continue;

 for (let j = 2; j <= Math.sqrt(number); j++) {
 if (number % j === 0) {
 // Not a prime number
 break;
 }
 }

 // If the loop finished without finding any factors, the number is prime
 if (j > Math.sqrt(number)) {
 console.log(number);
 }
 }
}

// Example usage:
let numbers = [3, 5, 7, 9, 11];
checkPrime(numbers); // Output: 3, 5, 7
```

