// Function to count the occurrences of a specific word in a string
int countWordOccurrences(const std::string &text, const std::string &word) {
    std::stringstream ss(text);
    std::string token;
    int count = 0;

    while (std::getline(ss, token, ' ')) {
        if (token == word) {
            ++count;
        }
    }

    return count;
}

int main() {
    // Check if the input file is provided
    if (int argc < 3) {
        std::cerr << "Usage: CountWords <filename> <word>\n";
        return 1;
    }

    const std::string filename = argv[1];
    const std::string word = argv[2];

    // Open the input file
    std::ifstream file(filename);

    if (!file.is_open()) {
        std::cerr << "Error: Unable to open the file.\n";
        return 1;
    }

    // Read the file line by line
    std::string line;
    int total_occurrences = 0;

    while (std::getline(file, line)) {
        total_occurrences += countWordOccurrences(line, word);
    }

    // Print the total number of occurrences
    std::cout << "The word '" << word << "' occurs " << total_occurrences << " times in the file.\n";

    return 0;
}
