        System.out.println(commonElements);  // Output: [2, 4]
    }

    public static List<Integer> findCommonElements(int[] arr1, int[] arr2) {
        // Convert arrays to 32-bit integers
        int n1 = (1 << arr1.length) - 1;
        int n2 = (1 << arr2.length) - 1;

        // Perform bitwise AND operation
        int result = n1 & n2;

        // Initialize result list
        List<Integer> common_elements = new ArrayList<>();

        // Iterate through the bits from right to left
        for (int i = arr1.length - 1; i >= 0; i--) {
            // If the bit is set, add the corresponding elements
            if ((result & (1 << i)) > 0) {
                if (arr1[i] == arr2[i]) {
                    common_elements.add(arr1[i]);
                }
            }
        }

        return common_elements;
    }
}
