\newcommand{\pieceN}{\chesspiece{N}}
\newcommand{\pieceB}{\chesspiece{B}}
\newcommand{\pieceQ}{\chesspiece{Q}}
\newcommand{\pieceK}{\chesspiece{K}}

% Define custom command for drawing the board
