```cpp
#include <iostream>
#include <string>
#include <regex>

std::string markdownToHtml(const std::string &markdown) {
    std::regex headingRegex(R"(# {1,6}(.*?)(?=\n(#|$)))");
    std::regex linkRegex(R"(\[(.*?)\]\((.*?)\))");

    std::string html = "<!DOCTYPE html>\n<html lang='en'>\n<head>\n  <meta charset='UTF-8'>\n  <meta name='viewport' content='width=device-width, initial-scale=1.0'>\n  <title>Document</title>\n</head>\n<body>\n";

    std::smatch match;
    while (std::regex_search(markdown, match, headingRegex)) {
        int level = match.size() > 1 ? match[1].length() + 1 : 1;
        std::string content = match[2];
        html += std::string(level * 2, "<h") + std::to_string(level) + ">" + content + "</h" + std::to_string(level) + ">\n";
        markdown.erase(match.position(), match.length());
    }

    while (std::regex_search(markdown, match, linkRegex)) {
        std::string linkText = match[1];
        std::string linkUrl = match[2];
        html += "<a href='" + linkUrl + "'>" + linkText + "</a>";
        markdown.erase(match.position(), match.length());
    }

    html += markdown;
    html += "</body>\n</html>";

    return html;
}

int main() {
    std::string markdown = "# Heading 1\n## Heading 2\n[Link Text](https://example.com)\n_Italic text_ **Bold text**";
    std::string html = markdownToHtml(markdown);
    std::cout << html << std::endl;

    return 0;
}
```

