    // Iterate through the first array
    for (const auto& num : arr1) {
        // If the number is in the second array, add it to the result
        if (std::find(arr2.begin(), arr2.end(), num) != arr2.end() && std::find(common_elements.begin(), common_elements.end(), num) == common_elements.end()) {
