        }
    }
    return htmlHeader;
}

// Helper function to convert Markdown italics and bold to HTML italics and bold
std::string convertMarkdownItalicsAndBoldToHtml(const std::string &markdownText) {
    std::string htmlText;
    for (size_t i = 0; i < markdownText.length(); i++) {
        if (markdownText[i] == '*' && markdownText[i + 1] == '*') {
            htmlText += "<b>" + markdownText.substr(i + 2, 1) + "</b>";
            i += 2;
        } else if (markdownText[i] == '_' && markdownText[i + 1] == '_') {
            htmlText += "<i>" + markdownText.substr(i + 2, 1) + "</i>";
            i += 2;
        } else {
            htmlText += markdownText[i];
        }
    }
    return htmlText;
}

// Function to convert Markdown formatted text to HTML formatted text
std::string markdownToHtml(const std::string &markdownText) {
    std::string htmlText;
    std::map<char, char> escapeChars = {{'&', "&amp;"}, {'<', "&lt;"}, {'>', "&gt;"}, {'"', "&quot;"}};

    // Escape special characters
    for (const auto &c : markdownText) {
        auto it = escapeChars.find(c);
        if (it != escapeChars.end()) {
            htmlText += it->second;
        } else {
            htmlText += c;
        }
    }

    // Convert Markdown elements to HTML
    std::regex headerRegex(R"(^#+ [^\n]*)");
    std::regex linkRegex(R"((\[.*\])(\((https?:\/\/[^\)]+)\)))");
    std::regex italicsAndBoldRegex(R"(((\*[^\*]*\*)|(\_[^\_]*_\))))");
    std::sregex_iterator headerIter(markdownText.cbegin(), markdownText.cend(), headerRegex);
    std::sregex_iterator linkIter(markdownText.cbegin(), markdownText.cend(), linkRegex);
    std::sregex_iterator italicsAndBoldIter(markdownText.cbegin(), markdownText.cend(), italicsAndBoldRegex);

    while (headerIter != markdownText.cend()) {
        htmlText += convertMarkdownHeaderToHtml(headerIter->str());
        ++headerIter;
    }

    while (linkIter != markdownText.cend()) {
        htmlText += convertMarkdownLinkToHtml(linkIter->str());
        ++linkIter;
    }

    while (italicsAndBoldIter != markdownText.cend()) {
        htmlText += convertMarkdownItalicsAndBoldToHtml(italicsAndBoldIter->str());
        ++italicsAndBoldIter;
    }

    return htmlText;
}

