void count_word_occurrences(const std::string &filename, const std::string &search_word) {
    std::ifstream file(filename);
    std::map<int, int> word_count;
    std::regex word_regex(R"(\b" + search_word + R"\b)");
    std::string line;
    int line_number = 1;

    if (file.is_open()) {
        while (std::getline(file, line)) {
