```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>

std::map<std::string, int> countWords(const std::string &filename) {
    std::map<std::string, int> word_count;
    std::ifstream file(filename);
    std::string line;

    if (file.is_open()) {
        while (std::getline(file, line)) {
            std::stringstream ss(line);
            std::string word;

            while (ss >> word) {
                std::transform(word.begin(), word.end(), word.begin(), ::tolower);
                ++word_count[word];
            }
        }
        file.close();
    }

    return word_count;
}

int main() {
    const std::string filename = "input.txt";
    auto word_count = countWords(filename);
    auto it = word_count.find("example");

    if (it != word_count.end()) {
        std::cout << "The word 'example' occurs " << it->second << " times in the file.\n";
    } else {
        std::cout << "The word 'example' was not found in the file.\n";
    }

    return 0;
}
