import { Client, Intents, SlashCommandBuilder } from 'discord.js';
import { REST } from '@discordjs/rest';
import { Routes } from 'discord-api-types/v10';

const client = new Client({ intents: [Intents.Guilds, Intents.GuildMessages, Intents.GuildMembers, Intents.MessageContent] });

