std::string markdownToHtml(const std::string &markdown) {
    std::string html;
    std::regex headingRegex(R"(# {1,6}(.*?)(?=\n(#|$)))");
    std::regex linkRegex(R"(\[(.*?)\]\((.*?)\))");

    std::sregex_token_iterator begin(markdown.cbegin(), markdown.cend(), headingRegex, -1);
    std::sregex_token_iterator end;

