        html += tag + heading + "</" + tag + ">\n";
        level++;
    }

    std::regex_iterator<std::string::const_iterator> linkIter(markdown.cbegin(), markdown.cend(), linkRegex);
    while (linkIter != markdown.cend()) {
        std::smatch match = *linkIter++;
        std::string linkText = match.str(1);
        std::string linkUrl = match.str(2);

        html += "<a href=\"" + linkUrl + "\">" + linkText + "</a>\n";
    }

    // Add other Markdown elements you want to convert here

    return html;
}

