```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <map>
#include <regex>

std::ifstream file;
std::string line, word_to_find, filename;
std::map<int, int> line_numbers;
std::regex word_regex(R"(\b" + word_to_find + R"\b)");

void count_word_occurrences(const std::string &line) {
    std::sregex_iterator search_begin(line.cbegin(), line.cend(), word_regex);
    std::sregex_iterator search_end;

    for (; search_begin != search_end; ++search_begin) {
        int line_number = line_numbers.empty() ? 1 : line_numbers.rbegin()->first;
        line_numbers[line_number]++;
    }
}

int main() {
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to find: ";
    std::cin >> word_to_find;

    file.open(filename);

    if (!file.is_open()) {
        std::cerr << "Error opening the file." << std::endl;
        return 1;
    }

    int line_number = 1;
    while (std::getline(file, line)) {
        count_word_occurrences(line);
        std::cout << "Line " << line_number++ << ":" << std::endl << line << std::endl;
    }

    std::cout << "\nWord '" << word_to_find << "' occurrences:" << std::endl;
    for (const auto &entry : line_numbers) {
        int count = entry.second;
        for (int i = 0; i < entry.first; ++i) {
            std::cout << line_number++ << " ";
        }
        std::cout << count << std::endl;
    }

    file.close();
    return 0;
}
