    def peek(self):
        if self.is_empty():
            return None
        return self.items[-1]

    def size(self):
        return len(self.items)

    def __str__(self):
        return str(self.items)

class Queue:
    def __init__(self):
        self.stack1 = Stack()
        self.stack2 = Stack()

    def enqueue(self, item):
        self.stack1.push(item)

    def dequeue(self):
        if not self.stack2.is_empty():
            popped_item = self.stack2.pop()
            return popped_item
        if not self.stack1.is_empty():
            popped_item = self.stack1.pop()
            return popped_item
        return None

    def is_empty(self):
        return self.stack1.is_empty() and self.stack2.is_empty()

    def __str__(self):
        return f"Queue is {self.stack1} and {self.stack2}"

