#include <iostream>
#include <string>

std::string convertMarkdownToHtml(const std::string& markdownText) {
    // HTML tags and their corresponding HTML characters
    const std::string htmlTags = (
        "<!DOCTYPE html>\n",
        "<html>\n",
        "<head>\n",
        "<title>\n",
        "</head>\n",
        "<body>\n",
        "</body>\n",
        "</html>"
    );

    // Split the Markdown text into lines
    std::string lines;
    for (char c : markdownText) {
        lines += c;
    }

    // Replace Markdown text with HTML
    for (size_t i = 0; i < lines.size(); ++i) {
        if (lines[i] == '\n') {
            lines[i] = '<p>';
        } else if (lines[i] == '\t') {
            lines[i] = '<strong>';
        } else if (lines[i] == '\t\n') {
            lines[i] = '</p>';
