#include <iostream>
#include <fstream>
#include <string>
#include <algorithm>

// Function to count occurrences of a word in a string
int countOccurrences(const std::string& line, const std::string& word) {
    int count = 0;
    std::string::size_type pos = 0;
    while ((pos = line.find(word, pos))!= std::string::npos) {
        count++;
        pos += word.length();
    }
    return count;
}

int main() {
    std::string filename;
    std::string word;

    std::cout << "Enter the filename: ";
    std::cin >> filename;

    std::cout << "Enter the word to count: ";
    std::cin >> word;

    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Unable to open file: " << filename << std::endl;
        return 1;
    }

    std::string line;
    int totalOccurrences = 0;

    while (std::getline(file, line)) {
        totalOccurrences += countOccurrences(line, word);
    }

    file.close();

    std::cout << "The word \"" << word << "\" occurred " << totalOccurrences << " times in the file." << std::endl;

    return 0;
}
