```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <algorithm>

int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Could not open the file: " << filename << std::endl;
        return -1;
    }

    std::string line;
    int wordCount = 0;

    while (std::getline(file, line)) {
        // Convert line to lowercase to make the search case insensitive
        std::transform(line.begin(), line.end(), line.begin(), ::tolower);
        std::string::size_type pos = 0;

        while ((pos = line.find(word, pos))!= std::string::npos) {
            // Check if the word is a whole word in the line
            if ((pos == 0 ||!std::isalpha(line[pos - 1])) && 
                std::isalpha(line[pos])) {
                wordCount++;
            }
            pos += word.size();
        }

        file >> line; // Consume the newline character
    }

    file.close();
    return wordCount;
}

int main() {
    std::string filename;
    std::string word;

    std::cout << "Enter the filename: ";
    std::cin >> filename;

    std::cout << "Enter the word to count: ";
    std::cin >> word;

    int wordOccurrences = countWordOccurrences(filename, word);

    if (wordOccurrences!= -1) {
        std::cout << "The word '" << word << "' occurred " << wordOccurrences << " times in the file." << std::endl;
    }

    return 0;
}
```

