function checkPrimes(numbers) {
    // Check if the input array has exactly 5 numbers
    if (numbers.length!== 5) {
        console.log("Input array must contain exactly 5 numbers.");
        return;
    }

    // Define a helper function to check if a number is prime
    function isPrime(num) {
        if (num <= 1) return false;
        for (let i = 2; i <= Math.sqrt(num); i++) {
            if (num % i === 0) return false;
        }
        return true;
    }

    // Iterate through the array and check for primes
    for (let i = 0; i < numbers.length; i++) {
        if (isPrime(numbers[i])) {
