```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <algorithm>

// Function to count the occurrences of a word in a line
int countOccurrences(const std::string& line, const std::string& word) {
    int count = 0;
    size_t pos = line.find(word);
    while (pos!= std::string::npos) {
        count++;
        pos = line.find(word, pos + word.length());
    }
    return count;
}

int main() {
    std::string filename;
    std::string word;

    // Ask user for the filename
    std::cout << "Enter the filename: ";
    std::cin >> filename;

    // Ask user for the word to search for
    std::cout << "Enter the word to search for: ";
    std::cin >> word;

    // Initialize the file pointer
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Could not open the file " << filename << std::endl;
        return 1;
    }

    int lineCount = 0;
    int wordCount = 0;

    std::string line;
    while (std::getline(file, line)) {
        lineCount++;

        // Count the occurrences of the word in the current line
        int lineOccurrences = countOccurrences(line, word);
        lineCount -= lineOccurrences; // Discard the word occurrences in the line
        wordCount += lineOccurrences; // Add the word occurrences to the total count
    }

    file.close();

    if (wordCount == 0) {
        std::cout << "The word \"" << word << "\" was not found in the file." << std::endl;
    } else {
        std::cout << "The word \"" << word << "\" occurred " << wordCount << " times in the file." << std::endl;
    }

    return 0;
}
```

