public class Solution {
    public boolean isPowerOfThree(int n) {
        if (n < 0) {
            return false;
        }
        int count = 0;
        while (n > 0) {
            if ((n & 1) == 1) {
                count++;
            }
            n >>= 1;
        }
        return count == 1;
    }

