// Function to count occurrences of a word in a line
int countOccurrences(const std::string &line, const std::string &word) {
    int count = 0;
    size_t pos = line.find(word, 0);
    while (pos!= std::string::npos) {
        // Check if the found word is a whole word (not part of a larger word)
        if ((pos == 0 ||!std::isalnum(line[pos - word.length() + 1])) {
            count++;
        }
        pos = line.find(word, pos + word.length());
    }
    return count;
}

int main() {
    std::string filename;
    std::string wordToCount;

    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Could not open the file: " << filename << std::endl;
        return 1;
    }

    std::string line;
    int totalOccurrences = 0;

    while (std::getline(file, line)) {
        totalOccurrences += countOccurrences(line, wordToCount);
    }

    file.close();

    std::cout << "The word \"" << wordToCount << "\" occurred " << totalOccurrences << " times in the file." << std::endl;

