       "CI": "npm ci -g cypress-cli"
     },
     "client": {
       "os": "Windows 10"
     },
     "config": {
       "port": 4000,
       "database": "cypress"
     },
     "test": {
       "container": "my-docker-test",
       "entry": "node index.js"
