                    SDL_RenderPresent(renderer);
                    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
                    snake->length = 1;
                    break;
                }
            }
        }
    }
}

void drawSnake(Snake *snake) {
    int screenWidth = SCREEN_WIDTH * 3;
    int screenHeight = SCREEN_HEIGHT * 3;
    SDL_SetRenderDrawColor(SDL.RenderColor::RGB(0, 255, 0, 255), 0, 0, 0);
