```cpp
#include <iostream>
#include <vector>
#include <cmath>

int main() {
    std::vector<double> vector = {3, 4};
    std::vector<double> normalizedVector;

    // Normalize the vector
    for (size_t i = 0; i < vector.size(); ++i) {
        double norm = std::sqrt(std::pow(vector[i], 2) + std::pow(vector[i+1], 2));
        normalizedVector.push_back(std::frac(vector[i], norm));
