```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <unordered_map>

int main() {
    std::string fileName, wordToCount;
    int count = 0;

    // Prompt the user for the filename
    std::cout << "Enter the filename: ";
    std::cin >> fileName;

    // Prompt the user for the word to count
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    // Open the file for reading
    std::ifstream file(fileName);
    if (file.is_open()!= std::ios::ok) {
        std::cerr << "Error opening file." << std::endl;
        return 1;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        // Convert the line to lowercase to handle case insensitivity
        std::string lowerLine(line);

        // Check if the word to count is in the current line
        if (lowerLine.find(wordToCount)!= std::string::npos) {
            count++;
        }
    }

    // Close the file
    file.close();

    // Print the result
    std::cout << "The word \"" << wordToCount << "\" occurred " << count << " times in the file." << std::endl;

    return 0;
}
