```java
public class PowerOfThree {

    public static boolean isPowerOfThree(int n) {
        // Handle edge cases
        if (n <= 0) {
            return false;
        }
        if (n == 1) {
            return true;
        }
        if (n == 3) {
            return true;
        }

        // Check if n can be written as 3^x where x is an integer
        // We need to find the largest integer x such that 3^x <= n
        // Iterate from 1 upwards and check for the condition
        for (int x = 1; x <= Math.sqrt(n); x++) {
            if (n % 3 == 0 && (n / 3) == Math.pow(3, x)) {
                return true;
            }
        }

        return false;
    }

    public static void main(String[] args) {
        // Example 1
        System.out.println(isPowerOfThree(27)); // true

        // Example 2
        System.out.println(isPowerOfThree(0)); // false

        // Example 3
        System.out.println(isPowerOfThree(-1)); // false
    }
}
