#include <iostream>
#include <fstream>
#include <string>
#include <vector>

int main() {
    std::string filename;
    std::string word;
    std::ifstream file(filename);

    if (file.is_open()) {
        std::string line;
        while (file >> line) {
            if (std::string::npos(line.find(word) == line.end())) {
                std::count_of(word) << line.length();
                std::string prev_line;
                file.seekg(file.tell() - 1);
                std::getline(file, prev_line);
                word += '\0';  // Append the newline character to the end of the word
                std::count_of(word) << line.length();
                file.seekg(file.tell() - 1);
            }
        }
        file.close();
        return 0;
    } else {
        std::cerr << "Error: " << filename << " is not a valid file." << std::endl;
        return 1;
    }

    return 0;
}
```

### Explanation:
