This program assumes the word is a single word and doesn't split it into multiple words (i.e., it treats "word" as a whole word).

```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <limits>
#include <sstream>

using namespace std;

int countWordOccurrences(const string& filename, string word) {
    ifstream file(filename);

    if (!file.is_open()) {
        cerr << "Error: Could not open file " << filename << endl;
        return -1;
    }

    string line;
    int count = 0;

    while ((line = file.readLine())!= NULL) {
        // Remove any punctuation at the beginning of the line
        line = line.replace(".", "").replace("!", "").replace("?", "").replace(":", "");
        line = line.replace("(", "").replace(")", "");
        line = line.replace(" ", "");
        line = line.replace("!", "");
        line = line.replace("?", "");

        // Check if the line matches the word we are counting
        if (line == word) {
            count++;
        }
    }

    file.close();
    return count;
}

int main() {
    string filename = "example.txt"; // Replace with your file name
    string word = "example"; // Replace with the word you want to count

    int result = countWordOccurrences(filename, word);
    if (result!= -1) {
