    function transferApproval(address _spender, uint256 _value) public returns (bool success) {
        require _spender!= address(0), "Invalid spender address";
        _value;
        _spender.transfer(_value, msg.sender);
        emit Transfer(_spender, msg.sender, _value);
        return true;
    }
