// Function to initialize display
void init_display(int width, int height) {
    // Set up the screen
    system("cls"); // Clear the screen
    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            system("cls"); // Clear the screen for each frame
        }
        for (int i = 0; i < 2; i++) {
            system("cls"); // Clear the screen
        }
    }
}

// Function to initialize colors
void init_colors() {
    // Set up colors
    set_background_color((unsigned char)1); // Blue background
    set_font_color(0); // Black text

