// Function to draw the snake
void draw_snake() {
    global snake_x, snake_y, direction;
    for (int i = 0; i < SNAKE_SIZE; i++) {
        system("move " + direction + " " + snake_x + " " + snake_y);
        if (i == 0) {
            system("fill " + color_snake);
        } else {
            system("fill " + color_snake + " ");
