    int y = keypad[2]; // 1: up, 2: down, 3: left, 4: right
    direction = (x * 10) + y;

    if (direction == 1) {
        snake_y = snake_y - 1;
    } else if (direction == 2) {
        snake_y = snake_y + 1;
    } else if (direction == 3) {
        snake_x = snake_x - 1;
    } else if (direction == 4) {
        snake_x = snake_x + 1;
    }

    if (snake_x < 0 || snake_x >= SCREEN_WIDTH || snake_y < 0 || snake_y >= SCREEN_HEIGHT) {
        system("color 0 0 0"); // Game over
        return -1;
    }
    return 0;
