function isPrime(num) {
    if (num <= 1) return false; // 1 and numbers less than or equal to 1 are not prime
    if (num <= 3) return true; // 2 and 3 are prime numbers

    // Check if the number is divisible by 2 or 3
    if (num % 2 === 0 || num % 3 === 0) return false;

    // Check from 5 to the square root of the number, incrementing by 2
    for (let i = 5; i * i <= num; i += 2) {
        if (num % i === 0 || num % (i + 2) === 0) return false;
    }

    return true;
}

function checkAndPrintPrimes(numbers) {
    if (numbers.length!== 5) {
        console.log("Array must contain exactly 5 numbers.");
        return;
    }

    for (let i = 0; i < 5; i++) {
        if (isPrime(numbers[i])) {
            console.log(numbers[i]);
        }
    }
}

// Example usage:
const inputArray = [2, 3, 5, 7, 11]; // Modify this array to test with different numbers
checkAndPrintPrimes(inputArray);
```

