    // Check if the file exists
    if (!std::ifstream.isfile(filename)) {
        std::cerr << "File not found!" << std::endl;
        return 1;
    }

    int count = 0;

    // Open the file and read line by line
    std::ifstream file(filename);
    while (std::getline(file, wordToCount)) {
        count++;
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word '" << wordToCount << "' occurs " << count << " times in the file." << std::endl;

    return 0;
}
