    SDL_SetRenderDrawColor(SDLColor(0, 255, 0), 255, 255);
    SDL_RenderClear(SDLWhite);
    SDL_RenderCopy(SDL_Render, p, NULL);
    SDL_RenderPresent();
}

void update_point(Point* p, int width, int height) {
    int x = p->x;
    int y = p->y;

    // Check for collisions with walls
