    // Check for collisions with the snake
    Point* previous = *p - 1; // Previous point in the snake
    if (previous!= NULL) {
        if (distance(p, previous) < 10) {
            direction = -1; // Turn left to avoid collision
        } else {
            direction = 1; // Move right
        }
    }

    p->x += direction;
    p->y += direction;

    // Check for head collision with food
