    int width = SCREEN_WIDTH;
    int height = SCREEN_HEIGHT;
    int x, y;
    int direction;
    Point food = create_point(-1, -1);

    while (1) {
        update_point(food, width, height);
        SDL_Delay(10); // Delay to control the animation speed

        SDL_RenderClear(SDLWhite);

        draw_snake(food, width, height);

        SDL_RenderPresent();

        if (key[SDLK_LEFT] && direction == 1) break;
        if (key[SDLK_RIGHT] && direction == -1) break;
        if (key[SDLK_UP] && direction == 0) break;
        if (key[SDLK_DOWN] && direction == 1) break;

        SDL_KeyState(SDLKS_A | SDLKS_B);

