function isPrime(n) {
    if (n <= 1) {
        return false;
    }
    for (let i = 2; i <= Math.sqrt(n); i++) {
        if (n % i === 0) {
            return false;
        }
    }
    return true;
}

function checkPrimeNumbers(arr) {
    if (arr.length !== 5) {
        throw new Error("Input array must contain exactly 5 numbers.");
    }
    arr.forEach(num => {
        if (isPrime(num)) {
            console.log(`The prime number is: ${num}`);
        }
    });
}

// Example usage:
