void countWordOccurrences(const std::string& filePath, const std::string& wordToFind) {
    std::unordered_map<std::string, int> wordCount;

    // Open the file in read mode
    std::ifstream file(filePath);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filePath << std::endl;
        return;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        // Normalize the word to find by converting to lowercase and removing punctuation
        std::string lowerLine = line;
        std::transform(lowerLine.begin(), lowerLine.end(), lowerLine.begin(), ::tolower);
        lowerLine.erase(std::remove_if(lowerLine.begin(), lowerLine.end(), ::isspace), lowerLine.end());

        // Increment the count for the word in the map
        if (wordCount.find(lowerLine) != wordCount.end()) {
            wordCount[lowerLine]++;
        } else {
            wordCount[lowerLine] = 1;
        }
    }

    // Output the word counts
    std::cout << "Word '" << wordToFind << "' found " << wordCount[wordToFind] << " times." << std::endl;

