// Function to count occurrences of a word in a line
int countWordOccurrences(const std::string& line, const std::string& word) {
    int count = 0;
    size_t pos = line.find(word, 0);
    while (pos != std::string::npos) {
        count++;
        pos = line.find(word, pos + word.length());
    }
    return count;
}

int main() {
    std::string filename;
    std::string word;

    // Get the filename and word from the user
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> word;

    // Open the file in read mode
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Could not open the file " << filename << std::endl;
        return 1;
    }

    std::string line;
    int totalOccurrences = 0;

    // Read the file line by line
    while (std::getline(file, line)) {
        // Count occurrences of the word in the current line
        int occurrences = countWordOccurrences(line, word);
        totalOccurrences += occurrences;
    }

    // Close the file
    file.close();

    // Output the total number of occurrences
    std::cout << "The word \"" << word << "\" occurs " << totalOccurrences << " times in the file." << std::endl;

