        fprintf(stderr, "Could not initialize SDL\n");
        exit(1);
    }
}

int main() {
    // Initialize SDL
    init(SDL_INIT_EVERYTHING);

    // Initialize the SDL image library
    if (png_Init()!= 0) {
        fprintf(stderr, "Could not initialize PNG library\n");
        exit(1);
    }

    // Load the snake image
    SDL_Surface *snakeImage = SDL_LoadImage("resources/image.png", NULL, SDL_SWSRC | SDL_SRCCOMPOSITE | SDL_DOUBLEBUF, 0, 0, 640, 480, NULL);
    if (!snakeImage) {
        exit(1);
    }

    // Load the background image
    SDL_Surface *backgroundImage = SDL_LoadImage("resources/background.png", NULL, SDL_SWSRC | SDL_SRCCOMPOSITE | SDL_DOUBLEBUF, 0, 0, 640, 480, NULL);
