        exit(1);
    }

    // Initialize the SDL sound library
    if (snd_init()!= 0) {
        fprintf(stderr, "Could not initialize sound library\n");
        exit(1);
    }

    // Initialize the SDL window
    SDL_Window window;
    SDL_SetVideoMode(640, 480, 32, 32);
    SDL_SetVideoMode(window, 640, 480, 32, 32);

    // Loop infinitely
    while (1) {
        // Clear the screen
        SDL_SetVideoMode(window, 640, 480, 32, 32);
        SDL_WM_SetDisplay(window, NULL);

        // Get the window size
        int width, height;
        if (window->GetWindowRect(window, NULL)) {
            width = window->GetWindowRect(window)[0];
            height = window->GetWindowRect(window)[1];
        }

