#include <string>
#include <html/HTML.h>

std::string formatMarkdownToHTML(std::string input) {
    std::string output;
    std::html::HTML html;
    std::string result;

    // Create a new HTML object
    html->createDocument(input);

    // Set the document's encoding to UTF-8
    html->setDocumentEncoding("UTF-8");

    // Get the text from the HTML document
    result = html->getDocumentText();

    // Return the formatted HTML string
    return result;
}

int main() {
    std::string input = "Hello, world! This is a test markdown text.\n";
    std::string output = formatMarkdownToHTML(input);

    std::cout << "HTML output: " << output << std::endl;
    return 0;
}
