import java.math.BigInteger;

public class PowerOfThree {

    public static boolean isPowerOfThree(int n) {
        // Check if n is negative
        if (n < 0) {
            return false;
        }

        // If n is 1, it is a power of 3
        if (n == 1) {
            return true;
        }

        // If n is 0, it is not a power of 3
        if (n == 0) {
            return false;
        }

        // If n is a non-negative integer, we need to find a factor x such that 3^x == n
        BigInteger number = new BigInteger(n);
        BigInteger x = new BigInteger("1");

        // We need to find a factor x such that 3^x == n
        while (x.compareTo(number) > 0) {
            x *= 3;
        }

        return true;
    }

    public static void main(String[] args) {
        System.out.println(isPowerOfThree(27)); // true
        System.out.println(isPowerOfThree(0)); // false
        System.out.println(isPowerOfThree(-1)); // false
    }
}
```

