const duck = {
  name: 'Duck',
  talk: function() {
    return 'Quack!';
  },
  fly: function() {
    return 'Wing-flap!';
  },
  food: function() {
    return 'Mud';
  },
  duck: function() {
    return 'Wing-flap!';
  }
};

const cat = {
