    return 'Chicken!';
  },
  dog: function() {
    return 'Woof!';
  }
};

const bird = {
  name: 'Bird',
  talk: function() {
    return 'Squawk!';
  },
  fly: function() {
    return 'Flap!';
  },
  food: function() {
    return 'Beetles!';
  },
  bird: function() {
    return 'Squawk!';
  }
};

const fish = {
  name: 'Fish',
  talk: function() {
    return 'Swim!';
  },
  fly: function() {
    return 'Swoop!';
  },
  food: function() {
    return 'Mud!';
  },
  fish: function() {
    return 'Swim!';
  }
};

const duck = duck();
const cat = cat();
const dog = dog();
const bird = bird();
const fish = fish();

const iscompatible = duckducking();
const iscompatible = catducking();
const iscompatible = dogducking();
const iscompatible = birdducking();
const iscompatible = fishducking();

console.log(iscompatible);
