std::string htmlFromMarkdown(const std::string& markdownText) {
    // Use the cctype library to check if a character is a valid HTML entity
    std::string htmlText;
    std::vector<char> htmlEntities;
    std::size_t htmlLength = 0;

    while (markdownText.size() > htmlLength) {
        std::string htmlEntity = htmlEntities.back();
        if (htmlEntity == null) {
            htmlText += htmlEntities.front();
            htmlEntities.push_back(htmlEntity);
            std::swap(htmlEntities.back(), htmlEntities.front());
        } else {
            htmlText += htmlEntity;
        }
        htmlLength++;
    }

    htmlText += markdownText.size();

    // Replace the last character (if it's a literal character)
    char lastChar = htmlText[htmlLength - 1];
    if (lastChar == '\n' || lastChar == '\r') {
        htmlText += '\n';
    }
    if (lastChar == '\n' || lastChar == '\r') {
        htmlText += '\r';
    }

    return htmlText;
}

