class QueueUsingStacks:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, item):
        # Push item onto stack2
        self.stack2.append(item)
        # If stack1 is empty, push all items from stack2 onto stack1
        if not self.stack1:
            self.stack1.extend(self.stack2)
        else:
            # If stack2 is empty, push all items from stack1 onto stack2
            if not self.stack2:
                self.stack2.extend(self.stack1)

    def dequeue(self):
        # If both stacks are empty, return None
        if not self.stack1 and not self.stack2:
            return None
        # If stack1 is empty, pop from stack2
        if not self.stack1:
            return self.stack2.pop()
        # If stack2 is empty, pop from stack1
        if not self.stack2:
            return self.stack1.pop()
        # Otherwise, pop the smallest element from either stack
        return self.stack1.pop()

    def peek(self):
        # If both stacks are empty, return None
        if not self.stack1 and not self.stack2:
            return None
        # Otherwise, peek the smallest element from either stack
        if not self.stack1:
            return self.stack2[-1]
        elif not self.stack2:
            return self.stack1[-1]
        else:
            return self.stack1[-1]

    def is_empty(self):
        # If both stacks are empty, return True
        if not self.stack1 and not self.stack2:
            return True
        # Otherwise, return False
        return not self.stack1 or not self.stack2

    def size(self):
        # If both stacks are empty, return 0
        if not self.stack1 and not self.stack2:
            return 0
        # Otherwise, return the size of the larger stack
        if not self.stack1:
            return len(self.stack2)
        elif not self.stack2:
            return len(self.stack1)
        else:
            return max(len(self.stack1), len(self.stack2))

