#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>

int countWordOccurrences(const std::string& filename, const std::string& word) {
    std::vector<std::string> words;
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Could not open file " << filename << std::endl;
        return 0;
    }

    std::string line;
    while (std::getline(file, line)) {
        words.push_back(line);
    }

    std::string word;
    std::string wordSet;
    int count = 0;

    for (const auto& wordSet : words) {
        if (wordSet == word) {
            count++;
        }
    }

    file.close();
    return count;
}

int main() {
    std::string filename;
    std::string word;

    std::cout << "Enter the filename: ";
    std::cin >> filename;

    std::cout << "Enter the word to count: ";
    std::cin >> word;

    int count = countWordOccurrences(filename, word);
    if (count > 0) {
        std::cout << "The word \"" << word << "\" occurs " << count << " times in the file." << std::endl;
    } else {
        std::cout << "The word \"" << word << "\" does not occur in the file." << std::endl;
