// Function to count occurrences of a specific word in a file
int countWordOccurrences(const std::string &fileName, const std::string &word) {
    std::vector<std::string> lines;
    std::ifstream file(fileName, std::ifstream::binary);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << fileName << std::endl;
        return -1;
    }

    lines.reserve(200); // Reserve space for 200 lines, adjust as needed
    while (std::getline(file, lines)) {
        if (std::getline(lines, lines.size() - 1, word)) {
            // Check if the word is a valid word
            if (std::isspace(word)) {
                std::cout << "Word '" << word << "' found in file: " << fileName << std::endl;
                return 1;
            }
        }
    }

    std::cout << "Word '" << word << "' not found in file: " << fileName << std::endl;
    file.close();
    return 0;
}

